#!/usr/bin/env python3
"""
D-TECH YouTube/TikTok Downloader CLI for Termux
Continuous CLI: enter URLs to download without restarting.
Automatically places media in shared Movies folder and triggers MediaStore scan.
"""
import os
import sys
import uuid
import base64
import subprocess
from datetime import datetime

# Preferred save location: shared Movies folder for gallery visibility
SHARED_MOVIES = os.path.expanduser("~/storage/shared/Movies")
# Fallbacks if Movies doesn't exist
def get_download_dir():
    if os.path.isdir(SHARED_MOVIES):
        return SHARED_MOVIES
    shared_download = os.path.expanduser("~/storage/shared/Download")
    if os.path.isdir(shared_download):
        return shared_download
    return os.getcwd()

DOWNLOAD_DIR = get_download_dir()
PERM_MARKER = os.path.expanduser("~/.termux_storage_permission_granted")
COOKIES_FILE = "cookies.txt"
# Base64-encoded cookies.txt (paste your encoded cookies here)
EMBEDDED_COOKIES = """
"""

# yt-dlp format selector: best video+audio
YT_DLP_FORMAT = 'bestvideo+bestaudio/best'


def ensure_storage_permission():
    if not os.path.isfile(PERM_MARKER):
        print("üîí Requesting storage permission...")
        subprocess.run(["termux-setup-storage"], check=False)
        open(PERM_MARKER, 'w').close()


def save_embedded_cookies():
    if not os.path.exists(COOKIES_FILE) and EMBEDDED_COOKIES.strip():
        with open(COOKIES_FILE, 'wb') as f:
            f.write(base64.b64decode(EMBEDDED_COOKIES.strip()))
        print("‚úÖ cookies.txt generated from embedded data.")


def trigger_media_scan(path):
    """
    Ask Android to scan the new media file so it appears in gallery apps.
    Requires termux-api or termux-media-scan available.
    """
    try:
        subprocess.run(["termux-media-scan", path], check=False)
    except FileNotFoundError:
        # fallback: broadcast intent
        subprocess.run([
            "am", "broadcast",
            "-a", "android.intent.action.MEDIA_SCANNER_SCAN_FILE",
            "-d", f"file://{path}"
        ], check=False)


def download_media(url: str, audio_only: bool = False):
    # Filename prefix with timestamp so modified time is now
    timestamp = datetime.now().strftime('%Y%m%d_%H%M%S')
    ext = 'mp3' if audio_only else 'mp4'
    filename = f"{timestamp}_{uuid.uuid4()}.{ext}"
    output_path = os.path.join(DOWNLOAD_DIR, filename)

    cmd = [
        'yt-dlp',
        '-f', YT_DLP_FORMAT,
        '--no-mtime',          # enforce current timestamp
        '-o', output_path,
        '--cookies', COOKIES_FILE,
        url
    ]
    if audio_only:
        cmd.extend(['--extract-audio', '--audio-format', 'mp3'])

    print(f"‚è≥ Downloading {'audio' if audio_only else 'video'} to {output_path}...")
    try:
        subprocess.run(cmd, check=True)
        if os.path.isfile(output_path):
            print(f"‚úÖ Download complete: {output_path}")
            # Trigger media scan so it shows in gallery
            trigger_media_scan(output_path)
            print("üîç Media scanned and should appear in your gallery.")
            print()
            return True
    except subprocess.CalledProcessError as e:
        print(f"‚ùå Download failed: {e}\n")
    return False


def main_loop():
    print(f"üåü D-TECH Downloader CLI (continuous mode) üåü")
    print(f"Media will be saved to: {DOWNLOAD_DIR}\n")
    print("Type 'mp3 <URL>' for audio or just paste <URL> for video. Type 'q' to quit.\n")
    while True:
        user_input = input("> ").strip()
        if not user_input:
            continue
        if user_input.lower() in ('q', 'quit', 'exit'):
            print("üëã Exiting.")
            break
        parts = user_input.split(None, 1)
        if parts[0].lower() == 'mp3' and len(parts) == 2:
            download_media(parts[1], audio_only=True)
        else:
            download_media(parts[0], audio_only=False)


if __name__ == '__main__':
    ensure_storage_permission()
    save_embedded_cookies()
    # ensure download dir exists
    os.makedirs(DOWNLOAD_DIR, exist_ok=True)
    main_loop()
